const { MODULE_LIST } = require('./broadlink.device.constants');

/**
 * @description Test if peripheral is managed by the device handler.
 * @param {Object} peripheralInfo - Peripheral information generated by Broadlink library.
 * @returns {boolean} Is peripheral managed ?
 * @example
 * deviceHandler.matchPeripheral({
 *  name: 'RM3 Pro Plus',
 *  address: '192.168.1.1',
 *  ...
 * });
 */
function matchPeripheral(peripheralInfo) {
  const { module } = peripheralInfo;
  return MODULE_LIST.includes(module);
}

module.exports = {
  matchPeripheral,
};
