const { BroadlinkDeviceRM2 } = require('broadlink-js');

/**
 * @description Build peripheral.
 * @param {Object} peripheralInfo - Peripheral information generated by Broadlink library.
 * @returns {Object} Broadlink peripheral.
 * @example
 * remoteHandler.buildPeripheral({
 *  name: 'RM3 Pro Plus',
 *  address: '192.168.1.1',
 *  ...
 * });
 */
function buildPeripheral(peripheralInfo) {
  const device = new BroadlinkDeviceRM2(peripheralInfo);

  const peripheral = {
    canLearn: true,
  };

  return { device, peripheral };
}

module.exports = {
  buildPeripheral,
};
